import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html'
})
export class HomeComponent implements OnInit {
  showModal = false;
  inscricaoForm!: FormGroup;

  decanatos: any[] = [];
  grupos: any[] = [];

  constructor(private fb: FormBuilder, private http: HttpClient) {}

  ngOnInit() {
    this.inscricaoForm = this.fb.group({
      cpf: ['', [Validators.required]],
      nome: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      telefone: ['', Validators.required],
      decanatoId: ['', Validators.required],
      grupoId: ['', Validators.required]
    });

    this.carregarDecanatos();
  }

  abrirModal() {
    this.showModal = true;
  }

  fecharModal() {
    this.showModal = false;
    this.inscricaoForm.reset();
  }

  carregarDecanatos() {
    this.http.get<any[]>('/api/decanatos').subscribe({
      next: (data) => (this.decanatos = data),
      error: (err) => console.error(err)
    });
  }

  carregarGrupos(event: Event) {
    const select = event.target as HTMLSelectElement | null;

    if (select && select.value) {
      const decanatoId = select.value;

      this.http.get<any[]>(`/api/decanatos/${decanatoId}/grupos`).subscribe({
        next: (data) => (this.grupos = data),
        error: (err) => console.error(err)
      });
    }
  }

  validarCpf() {
    const cpf = this.inscricaoForm.get('cpf')?.value;
    if (cpf && cpf.length === 11) {
      this.http.get<any>(`/api/inscritos/${cpf}`).subscribe({
        next: (data) => {
          if (data) {
            this.inscricaoForm.patchValue({
              nome: data.nome,
              email: data.email,
              telefone: data.telefone,
              decanatoId: data.decanatoId,
              grupoId: data.grupoId
            });
            this.carregarGrupos(data.decanatoId);
          }
        },
        error: (err) => {
          console.log('CPF não encontrado, insira os dados manualmente.', err);
        }
      });
    }
  }

  salvar() {
    if (this.inscricaoForm.valid) {
      this.http.post('/api/inscritos', this.inscricaoForm.value).subscribe({
        next: () => {
          alert('Inscrição realizada com sucesso!');
          this.fecharModal();
        },
        error: (err) => console.error(err)
      });
    }
  }
}
