<!-- Modal de Inscrição -->
<div *ngIf="showModal" class="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
    <div class="bg-white rounded-lg shadow-lg w-full max-w-md p-6 relative">

        <!-- Botão fechar -->
        <button (click)="fecharModal()" 
        class="absolute top-2 right-2 text-gray-500 hover:text-gray-800">
        ✖
    </button>

    <h2 class="text-2xl font-bold mb-4">Inscrição</h2>

    <form [formGroup]="inscricaoForm" (ngSubmit)="salvar()">

        <!-- CPF -->
        <input type="text" 
        placeholder="CPF" 
        formControlName="cpf" 
        (blur)="validarCpf()"
        class="w-full border p-2 mb-3 rounded" />
        <div *ngIf="inscricaoForm.get('cpf')?.invalid && inscricaoForm.get('cpf')?.touched" 
        class="text-red-500 text-sm">CPF obrigatório</div>

        <!-- Nome -->
        <input type="text" placeholder="Nome" formControlName="nome"
        class="w-full border p-2 mb-3 rounded" />

        <!-- Email -->
        <input type="email" placeholder="Email" formControlName="email"
        class="w-full border p-2 mb-3 rounded" />

        <!-- Telefone -->
        <input type="text" placeholder="Telefone" formControlName="telefone"
        class="w-full border p-2 mb-3 rounded" />

        <!-- Decanato -->
        <select formControlName="decanatoId" 
        (change)="carregarGrupos($event)" 
        class="w-full border p-2 mb-3 rounded">
        <option value="">Selecione o Decanato</option>
        <option *ngFor="let d of decanatos" [value]="d.id">{{ d.nome }}</option>
    </select>

    <!-- Grupo de Oração -->
    <select formControlName="decanatoId" 
        (change)="carregarGrupos($event)" 
        class="w-full border p-2 mb-3 rounded">
  <option value="">Selecione o Decanato</option>
  <option *ngFor="let d of decanatos" [value]="d.id">{{ d.nome }}</option>
</select>
<button type="submit" 
[disabled]="inscricaoForm.invalid"
class="w-full bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600">
Enviar
</button>
</form>
</div>
