import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, FormArray, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-evento-form',
  templateUrl: './evento-form.component.html'
})
export class EventoFormComponent implements OnInit {
  eventoForm!: FormGroup;
  titulo = 'Novo Evento';
  abaAtiva = 'dados'

  constructor(
    private fb: FormBuilder,
    private route: ActivatedRoute,
    private router: Router,
    private http: HttpClient

  ) {}

  ngOnInit(): void {
    this.eventoForm = this.fb.group({
      nome: ['', Validators.required],
      slug: [''],
      dataInicio: ['', Validators.required],
      dataFim: ['', Validators.required],
      organizadorNome: ['', Validators.required],
      organizadorEmail: ['', [Validators.required, Validators.email]],
      organizadorContato: ['', Validators.required],
      status: ['Criado', Validators.required],
      sobre: this.fb.group({
        titulo: ['Sobre', Validators.required],
        conteudo: this.fb.array([])
      }),
      local: this.fb.group({
        imagemMapa: [''],
        endereco: [''],
        complemento: [''],
        bairro: [''],
        cidade: [''],
        estado: ['']
      }),
      pregadores: this.fb.array([]),
      programacao: this.fb.array([]),
      informacoesAdicionais: this.fb.group({
        texto: this.fb.array([])
      }),
    });

    // slug automático
    this.eventoForm.get('nome')?.valueChanges.subscribe((nome: string) => {
      this.eventoForm.patchValue({
        slug: this.slugify(nome)
      }, { emitEvent: false });
    });

    // se for edição
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.titulo = 'Editar Evento';
      this.loadEvento(id);
    }
  }

  // -------- SOBRE --------
  get conteudoSobre(): FormArray {
    return this.eventoForm.get('sobre.conteudo') as FormArray;
  }

  addConteudoSobre(valor: string = '') {
    this.conteudoSobre.push(this.fb.control(valor, Validators.required));
  }

  removeConteudoSobre(i: number) {
    this.conteudoSobre.removeAt(i);
  }

  // -------- PREGADORES --------
  get pregadores(): FormArray {
    return this.eventoForm.get('pregadores') as FormArray;
  }

  addPregador(p: any = { nome: '', foto: '', descricao: '' }) {
    this.pregadores.push(
      this.fb.group({
        nome: [p.nome, Validators.required],
        foto: [p.foto, Validators.required],
        descricao: [p.descricao || '']
      })
    );
  }

  removePregador(i: number) {
    this.pregadores.removeAt(i);
  }

  // -------- PROGRAMAÇÃO --------
  get programacao(): FormArray {
    return this.eventoForm.get('programacao') as FormArray;
  }

  addProgramacao(item: any = { dia: '', descricao: '' }) {
    this.programacao.push(
      this.fb.group({
        dia: [item.dia, Validators.required],
        descricao: [item.descricao, Validators.required]
      })
    );
  }

  // -------- INFORMACOES ADICIONAIS --------
  get textoInformacoesAdicionais(): FormArray {
    return this.eventoForm.get('informacoesAdicionais.texto') as FormArray;
  }

  addTextoInformacoesAdicionais(valor: string = '') {
    this.textoInformacoesAdicionais.push(this.fb.control(valor, Validators.required));
  }

  removeInformacaoAdicional(i: number) {
    this.textoInformacoesAdicionais.removeAt(i);
  }

  removeProgramacao(i: number) {
    this.programacao.removeAt(i);
  }

  getConteudoSobre(): FormArray {
    return this.eventoForm.get('sobre.conteudo') as FormArray;
  }

  getPregadores(): FormArray {
    return this.eventoForm.get('pregadores') as FormArray;
  }

  getProgramacao(): FormArray {
    return this.eventoForm.get('programacao') as FormArray;
  }

  getTextoInformacoesAdicionais(): FormArray {
    return this.eventoForm.get('informacoesAdicionais.texto') as FormArray;
  }



  // -------- MOCK DE EDIÇÃO --------
  loadEvento(id: string) {
    this.http.get<any>('assets/eventos.json').subscribe({
      next: (dados: any[]) => {
        // se o JSON contiver vários eventos, filtra
        const evento = Array.isArray(dados) 
        ? dados.find(e => e.id == id)
        : dados;

        if (!evento) {
          console.error('Evento não encontrado!');
          return;
        }

        // Preenche campos simples
        this.eventoForm.patchValue({
          nome: evento.nome,
          slug: evento.slug,
          dataInicio: evento.dataInicio,
          dataFim: evento.dataFim,
          organizadorNome: evento.organizadorNome,
          organizadorEmail: evento.organizadorEmail,
          organizadorContato: evento.organizadorContato,
          status: evento.status,
          sobre: {
            titulo: evento.sobre?.titulo
          },
          local: evento.local || {}
        });

        // limpa arrays
        this.getConteudoSobre().clear();
        this.getPregadores().clear();
        this.getProgramacao().clear();
        this.getTextoInformacoesAdicionais().clear();

        // popula arrays
        (evento.sobre?.conteudo || []).forEach((c: string | undefined) => this.addConteudoSobre(c));
        (evento.pregadores || []).forEach((p: any) => this.addPregador(p));
        (evento.programacao || []).forEach((p: any) => this.addProgramacao(p));
        (evento.textoInformacoesAdicionais || []).forEach((info: string | undefined) => this.addTextoInformacoesAdicionais(info));
        console.log(this.textoInformacoesAdicionais)
      },
      error: (err) => {
        console.error('Erro ao carregar evento:', err);
      }
    });
  }

  salvar() {
    if (this.eventoForm.valid) {
      console.log('Evento salvo:', this.eventoForm.value);
      this.router.navigate(['/admin/eventos']);
    } else {
      this.eventoForm.markAllAsTouched();
    }
  }

  cancelar() {
    this.router.navigate(['/admin/eventos']);
  }

  private slugify(text: string): string {
    return text
    .toString()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .toLowerCase()
    .trim()
    .replace(/[^a-z0-9\s-]/g, '')
    .replace(/\s+/g, '-')
    .replace(/-+/g, '-');
  }
}
<div class="max-w-5xl mx-auto p-6 bg-white shadow rounded">
    <h2 class="text-2xl font-bold mb-6">{{ titulo }}</h2>

    <form [formGroup]="eventoForm" (ngSubmit)="salvar()">

        <!-- Tabs -->
        <ul class="flex border-b mb-6">
            <li class="mr-4">
                <a class="cursor-pointer py-2 px-4 block"
                [class.border-b-2]="abaAtiva === 'dados'"
                (click)="abaAtiva = 'dados'">Dados do Evento</a>
            </li>
            <li class="mr-4">
                <a class="cursor-pointer py-2 px-4 block"
                [class.border-b-2]="abaAtiva === 'sobre'"
                (click)="abaAtiva = 'sobre'">Sobre</a>
            </li>
            <li class="mr-4">
                <a class="cursor-pointer py-2 px-4 block"
                [class.border-b-2]="abaAtiva === 'pregadores'"
                (click)="abaAtiva = 'pregadores'">Pregadores</a>
            </li>
            <li>
                <a class="cursor-pointer py-2 px-4 block"
                [class.border-b-2]="abaAtiva === 'programacao'"
                (click)="abaAtiva = 'programacao'">Programação</a>
            </li>
            <li>
                <a class="cursor-pointer py-2 px-4 block"
                [class.border-b-2]="abaAtiva === 'local'"
                (click)="abaAtiva = 'local'">Local</a>
            </li>
            <li>
                <a class="cursor-pointer py-2 px-4 block"
                [class.border-b-2]="abaAtiva === 'informacoesAdicionais'"
                (click)="abaAtiva = 'informacoesAdicionais'">Informações Adicionais</a>
            </li>
        </ul>

        <!-- Aba Dados do Evento -->
        <div *ngIf="abaAtiva === 'dados'" class="space-y-4">
            <div>
                <label class="block font-medium">Nome do Evento</label>
                <input formControlName="nome" class="border p-2 w-full rounded" />
            </div>
            <div>
                <label class="block font-medium">Slug (URL)</label>
                <input formControlName="slug" class="border p-2 w-full rounded" readonly />
            </div>
            <div class="grid grid-cols-2 gap-4">
                <div>
                    <label class="block font-medium">Data Início</label>
                    <input type="date" formControlName="dataInicio" class="border p-2 w-full rounded" />
                </div>
                <div>
                    <label class="block font-medium">Data Fim</label>
                    <input type="date" formControlName="dataFim" class="border p-2 w-full rounded" />
                </div>
            </div>
            <div>
                <label class="block font-medium">Organizador</label>
                <input formControlName="organizadorNome" class="border p-2 w-full rounded" />
            </div>
            <div>
                <label class="block font-medium">E-mail</label>
                <input type="email" formControlName="organizadorEmail" class="border p-2 w-full rounded" />
            </div>
            <div>
                <label class="block font-medium">Contato</label>
                <input formControlName="organizadorContato" class="border p-2 w-full rounded" />
            </div>
            <div>
                <label class="block font-medium">Status</label>
                <select formControlName="status" class="border p-2 w-full rounded">
                    <option value="Criado">Criado</option>
                    <option value="Publicado">Publicado</option>
                </select>
            </div>
        </div>

        <!-- Aba Sobre -->
        <div *ngIf="abaAtiva === 'sobre'" formGroupName="sobre">
            <div>
                <label class="block font-medium">Título</label>
                <input formControlName="titulo" class="border p-2 w-full rounded" />
            </div>
            <div class="mt-4">
                <label class="block font-medium">Conteúdo</label>
                <div formArrayName="conteudo">
                    <div *ngFor="let c of conteudoSobre.controls; let i = index" class="flex items-center space-x-2 mt-2">
                        <input [formControlName]="i" class="border p-2 w-full rounded" />
                        <button type="button" class="bg-red-500 text-white px-3 py-1 rounded" (click)="removeConteudoSobre(i)">X</button>
                    </div>
                </div>
                <button type="button" class="mt-3 bg-green-500 text-white px-4 py-2 rounded" (click)="addConteudoSobre()">+ Adicionar Texto</button>
            </div>
        </div>

        <!-- Aba Pregadores -->
        <div *ngIf="abaAtiva === 'pregadores'" formArrayName="pregadores">
            <div *ngFor="let p of pregadores.controls; let i = index" [formGroupName]="i" class="border p-4 rounded mb-4">
                <div>
                    <label class="block font-medium">Nome</label>
                    <input formControlName="nome" class="border p-2 w-full rounded" />
                </div>
                <div>
                    <label class="block font-medium">Foto (URL)</label>
                    <input formControlName="foto" class="border p-2 w-full rounded" />
                </div>
                <div>
                    <label class="block font-medium">Descrição</label>
                    <textarea formControlName="descricao" class="border p-2 w-full rounded"></textarea>
                </div>
                <button type="button" class="mt-2 bg-red-500 text-white px-4 py-1 rounded" (click)="removePregador(i)">Remover</button>
            </div>
            <button type="button" class="bg-green-500 text-white px-4 py-2 rounded" (click)="addPregador()">+ Adicionar Pregador</button>
        </div>

        <!-- Aba Programação -->
        <div *ngIf="abaAtiva === 'programacao'" formArrayName="programacao">
            <div *ngFor="let p of programacao.controls; let i = index" [formGroupName]="i" class="border p-4 rounded mb-4">
                <div>
                    <label class="block font-medium">Dia</label>
                    <input type="date" formControlName="dia" class="border p-2 w-full rounded" />
                </div>
                <div>
                    <label class="block font-medium">Descrição</label>
                    <input formControlName="descricao" class="border p-2 w-full rounded" />
                </div>
                <button type="button" class="mt-2 bg-red-500 text-white px-4 py-1 rounded" (click)="removeProgramacao(i)">Remover</button>
            </div>
            <button type="button" class="bg-green-500 text-white px-4 py-2 rounded" (click)="addProgramacao()">+ Adicionar Programação</button>
        </div>

        <!-- Aba Local -->
        <div *ngIf="abaAtiva === 'local'" formGroupName="local" class="space-y-4">
            <div>
                <label class="block font-medium">Imagem (Google Maps)</label>
                <input formControlName="imagemMapa" placeholder="URL da imagem do mapa" class="border p-2 w-full rounded" />
            </div>

            <div>
                <label class="block font-medium">Endereço</label>
                <input formControlName="endereco" class="border p-2 w-full rounded" />
            </div>

            <div>
                <label class="block font-medium">Complemento</label>
                <input formControlName="complemento" class="border p-2 w-full rounded" />
            </div>

            <div>
                <label class="block font-medium">Bairro</label>
                <input formControlName="bairro" class="border p-2 w-full rounded" />
            </div>

            <div class="flex gap-2">
                <div class="w-1/2">
                    <label class="block font-medium">Cidade</label>
                    <input formControlName="cidade" class="border p-2 w-full rounded" />
                </div>
                <div class="w-1/2">
                    <label class="block font-medium">Estado</label>
                    <input formControlName="estado" class="border p-2 w-full rounded" />
                </div>
            </div>
        </div>

        <!-- Aba Informações Adicionais -->
        <div *ngIf="abaAtiva === 'informacoesAdicionais'" formArrayName="informacoesAdicionais">
            <div class="mt-4">
                <label class="block font-medium">Conteúdo</label>
                <div formArrayName="texto">
                    <div *ngFor="let c of textoInformacoesAdicionais.controls; let i = index" class="flex items-center space-x-2 mt-2">
                        <input [formControlName]="i" class="border p-2 w-full rounded" />
                        <button type="button" class="bg-red-500 text-white px-3 py-1 rounded" (click)="removeConteudoSobre(i)">X</button>
                    </div>
                </div>
                <button type="button" class="mt-3 bg-green-500 text-white px-4 py-2 rounded" (click)="addConteudoSobre()">+ Adicionar Texto</button>
            </div>
        </div>

        <!-- Botões -->
        <div class="flex justify-end space-x-4 mt-6">
            <button type="button" class="px-6 py-2 bg-gray-400 text-white rounded" (click)="cancelar()">Cancelar</button>
            <button type="submit" class="px-6 py-2 bg-pink-600 text-white rounded">Salvar</button>
        </div>
    </form>
</div>
