import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-evento-form',
  templateUrl: './evento-form.component.html'
})
export class EventoFormComponent implements OnInit {
  eventoForm!: FormGroup;
  titulo = 'Novo Evento';

  constructor(
    private fb: FormBuilder,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.eventoForm = this.fb.group({
      nome: ['', Validators.required],
      dataInicio: ['', Validators.required],
      dataFim: ['', Validators.required],
      ativo: [true],
      sections: this.fb.array([])
    });

    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.titulo = 'Editar Evento';
      // Aqui você chamaria seu service para carregar o evento do backend
      // Exemplo mock:
      this.eventoForm.patchValue({
        nome: 'Congresso Jovem',
        dataInicio: '2025-09-22',
        dataFim: '2025-09-25',
        ativo: true
      });
      this.sections.push(
        this.fb.group({
          titulo: ['Inscrições'],
          ativo: [true],
          conteudoJson: ['{}']
        })
      );
    }
  }

  get sections(): FormArray {
    return this.eventoForm.get('sections') as FormArray;
  }

  addSection() {
    this.sections.push(
      this.fb.group({
        titulo: ['', Validators.required],
        ativo: [true],
        conteudoJson: ['{}']
      })
    );
  }

  removeSection(index: number) {
    this.sections.removeAt(index);
  }

  salvar() {
    if (this.eventoForm.valid) {
      console.log('Evento salvo:', this.eventoForm.value);
      // Aqui você chamaria o service para salvar no backend
      this.router.navigate(['/admin/eventos']);
    } else {
      this.eventoForm.markAllAsTouched();
    }
  }

  cancelar() {
    this.router.navigate(['/admin/eventos']);
  }
}
