import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-evento-landing',
  templateUrl: './evento-landing.component.html',
  styleUrls: ['./evento-landing.component.scss']
})
export class EventoLandingComponent implements OnInit {
  evento: any;
  showModal = false;
  inscricaoForm: FormGroup;

  decanatos = [
    { id: 1, nome: 'Decanato Norte' },
    { id: 2, nome: 'Decanato Sul' }
  ];

  grupos: any[] = [];

  constructor(
    private route: ActivatedRoute,
    private http: HttpClient,
    private fb: FormBuilder
  ) {
    this.inscricaoForm = this.fb.group({
      cpf: ['', Validators.required],
      nome: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      telefone: ['', Validators.required],
      decanatoId: ['', Validators.required],
      grupoOracaoId: ['', Validators.required]
    });
  }

  ngOnInit(): void {
    const slug = this.route.snapshot.paramMap.get('slug');

    this.http.get<any[]>('assets/eventos.json').subscribe(eventos => {
      this.evento = eventos.find(e => e.slug === slug);
    });
  }

  abrirModal() {
    this.showModal = true;
  }

  fecharModal() {
    this.showModal = false;
  }

  validarCpf() {
    const cpf = this.inscricaoForm.get('cpf')?.value;
    // aqui você chamaria o backend para validar CPF e carregar dados se já existir
    console.log('Validar CPF:', cpf);
  }

  carregarGrupos(event: Event) {
  const decanatoId = (event.target as HTMLSelectElement).value;
    // mock de grupos para cada decanato
    if (decanatoId == '1') {
      this.grupos = [
        { id: 1, nome: 'Grupo de Oração São José' },
        { id: 2, nome: 'Grupo de Oração Nossa Senhora Aparecida' }
      ];
    } else if (decanatoId == '2') {
      this.grupos = [
        { id: 3, nome: 'Grupo de Oração Santo Antônio' },
        { id: 4, nome: 'Grupo de Oração São Miguel' }
      ];
    } else {
      this.grupos = [];
    }
  }

  salvar() {
    if (this.inscricaoForm.valid) {
      console.log('Inscrição salva:', this.inscricaoForm.value);
      this.fecharModal();
    } else {
      this.inscricaoForm.markAllAsTouched();
    }
  }
}
<div *ngIf="evento" class="bg-gradient-to-b from-pink-200 to-gray-100 min-h-screen">

    <!-- Navbar -->
    <nav class="flex justify-between items-center p-4 shadow-md bg-white/80 fixed top-0 w-full z-10">
        <div class="text-xl font-bold text-pink-600">RCC LONDRINA</div>
        <ul class="flex space-x-6 text-gray-700 font-medium">
            <li><a routerLink="." fragment="sobre" class="hover:text-pink-600">Sobre</a></li>
            <li><a routerLink="." fragment="pregadores" class="hover:text-pink-600">Pregadores</a></li>
            <li><a routerLink="." fragment="programacao" class="hover:text-pink-600">Programação</a></li>
        </ul>
        <button (click)="abrirModal()" class="border px-4 py-2 rounded-full hover:bg-pink-100">
            Inscrições
        </button>
    </nav>

    <!-- Banner -->
    <div class="text-center pt-32 px-4" >
        <h1 class="text-4xl md:text-6xl font-extrabold tracking-tight">
            {{ evento.nome }}
        </h1>
        <h2 class="text-2xl md:text-4xl font-semibold mt-4 text-pink-600">
            {{ evento.organizadorNome }}
        </h2>
        <p class="mt-6 text-lg text-gray-700">
            De <b>{{ evento.dataInicio }}</b> a <b>{{ evento.dataFim }}</b>
        </p>
        <button (click)="abrirModal()" 
        class="mt-8 bg-pink-500 text-white px-8 py-3 rounded-full shadow-lg hover:bg-pink-600 transition">
        INSCREVA-SE
    </button>
</div>

<section id="sobre" class="mt-5">
    <h2>{{ evento.sobre.titulo }}</h2>
    <div *ngFor="let paragrafo of evento.sobre.conteudo">
      <p>{{ paragrafo }}</p>
    </div>
  </section>

<!-- Seção Pregadores -->
<section id="pregadores" class="py-20 bg-white">
    <div class="max-w-6xl mx-auto text-center">
        <h3 class="text-3xl font-bold mb-6">Pregadores</h3>
        <div class="grid md:grid-cols-3 gap-8">
            <div *ngFor="let p of evento.pregadores" class="p-6 shadow-lg rounded-lg bg-gray-50">
                <img [src]="p.foto" [alt]="p.nome" class="mx-auto rounded-full mb-4 w-32 h-32 object-cover">
                <h4 class="font-bold text-lg">{{ p.nome }}</h4>
                <p class="text-sm text-gray-600">{{ p.descricao }}</p>
            </div>
        </div> 
    </div>
</section>

<!-- Seção Programação -->
<section id="programacao" class="py-20 max-w-4xl mx-auto text-center">
    <h3 class="text-3xl font-bold mb-6">Programação</h3>
    <ul class="text-left space-y-4">
        <li *ngFor="let p of evento.programacao" class="p-4 border rounded-lg bg-white shadow">
            <b>{{ p.dia }}</b> - {{ p.descricao }}
        </li>
    </ul>
</section>

<!-- Footer -->
<footer class="bg-gray-800 text-white text-center py-6">
    <p>© 2025 RCC Londrina - Todos os direitos reservados</p>
</footer>
</div>

<!-- Modal de Inscrição -->
<div *ngIf="showModal" class="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
    <div class="bg-white rounded-lg shadow-lg w-full max-w-md p-6 relative max-h-[90vh] overflow-y-auto">

        <!-- Botão fechar -->
        <button (click)="fecharModal()" 
        class="absolute top-2 right-2 text-gray-500 hover:text-gray-800">
        ✖
    </button>

    <h2 class="text-2xl font-bold mb-4">Inscrição</h2>

    <form [formGroup]="inscricaoForm" (ngSubmit)="salvar()">

        <!-- CPF -->
        <input type="text" 
        placeholder="CPF" 
        formControlName="cpf" 
        (blur)="validarCpf()"
        class="w-full border p-2 mb-3 rounded" />
        <div *ngIf="inscricaoForm.get('cpf')?.invalid && inscricaoForm.get('cpf')?.touched" 
        class="text-red-500 text-sm">CPF obrigatório</div>

        <!-- Nome -->
        <input type="text" placeholder="Nome" formControlName="nome"
        class="w-full border p-2 mb-3 rounded" />

        <!-- Email -->
        <input type="email" placeholder="Email" formControlName="email"
        class="w-full border p-2 mb-3 rounded" />

        <!-- Telefone -->
        <input type="text" placeholder="Telefone" formControlName="telefone"
        class="w-full border p-2 mb-3 rounded" />

        <!-- Decanato -->
        <select formControlName="decanatoId" 
        (change)="carregarGrupos($event)" 
        class="w-full border p-2 mb-3 rounded">
        <option value="">Selecione o Decanato</option>
        <option *ngFor="let d of decanatos" [value]="d.id">{{ d.nome }}</option>
    </select>

    <!-- Grupo de Oração -->
    <select formControlName="grupoOracaoId" 
    class="w-full border p-2 mb-3 rounded">
    <option value="">Selecione o Grupo de Oração</option>
    <option *ngFor="let g of grupos" [value]="g.id">{{ g.nome }}</option>
</select>

<button type="submit" 
[disabled]="inscricaoForm.invalid"
class="w-full bg-pink-500 text-white px-4 py-2 rounded hover:bg-pink-600">
Enviar
</button>
</form>
</div>
</div>
