import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-evento-landing',
  templateUrl: './evento-landing.component.html',
  styleUrls: ['./evento-landing.component.scss']
})
export class EventoLandingComponent implements OnInit {
  evento: any;
  showModal = false;
  inscricaoForm: FormGroup;

  decanatos = [
    { id: 1, nome: 'Decanato Norte' },
    { id: 2, nome: 'Decanato Sul' }
  ];

  grupos: any[] = [];

  constructor(
    private route: ActivatedRoute,
    private http: HttpClient,
    private fb: FormBuilder
  ) {
    this.inscricaoForm = this.fb.group({
      cpf: ['', Validators.required],
      nome: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      telefone: ['', Validators.required],
      decanatoId: ['', Validators.required],
      grupoOracaoId: ['', Validators.required]
    });
  }

  ngOnInit(): void {
    const slug = this.route.snapshot.paramMap.get('slug');

    this.http.get<any[]>('assets/eventos.json').subscribe(eventos => {
      this.evento = eventos.find(e => e.slug === slug);
    });
  }

  abrirModal() {
    this.showModal = true;
  }

  fecharModal() {
    this.showModal = false;
  }

  validarCpf() {
    const cpf = this.inscricaoForm.get('cpf')?.value;
    // aqui você chamaria o backend para validar CPF e carregar dados se já existir
    console.log('Validar CPF:', cpf);
  }

  carregarGrupos(event: Event) {
  const decanatoId = (event.target as HTMLSelectElement).value;
    // mock de grupos para cada decanato
    if (decanatoId == '1') {
      this.grupos = [
        { id: 1, nome: 'Grupo de Oração São José' },
        { id: 2, nome: 'Grupo de Oração Nossa Senhora Aparecida' }
      ];
    } else if (decanatoId == '2') {
      this.grupos = [
        { id: 3, nome: 'Grupo de Oração Santo Antônio' },
        { id: 4, nome: 'Grupo de Oração São Miguel' }
      ];
    } else {
      this.grupos = [];
    }
  }

  salvar() {
    if (this.inscricaoForm.valid) {
      console.log('Inscrição salva:', this.inscricaoForm.value);
      this.fecharModal();
    } else {
      this.inscricaoForm.markAllAsTouched();
    }
  }
}
