import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-evento-form',
  templateUrl: './evento-form.component.html'
})
export class EventoFormComponent implements OnInit {
  eventoForm!: FormGroup;
  titulo = 'Novo Evento';

  constructor(
    private fb: FormBuilder,
    private route: ActivatedRoute,
    private router: Router,
    private http: HttpClient

  ) {}

  ngOnInit(): void {
    this.eventoForm = this.fb.group({
      nome: ['', Validators.required],
      slug: [''], // novo campo
      dataInicio: ['', Validators.required],
      dataFim: ['', Validators.required],
      organizadorNome: ['', Validators.required],
      organizadorEmail: ['', [Validators.required, Validators.email]],
      organizadorContato: ['', Validators.required],
      status: ['Criado', Validators.required],
      sections: this.fb.array([])
    });

    // gera slug automaticamente ao mudar o nome
    this.eventoForm.get('nome')?.valueChanges.subscribe((nome: string) => {
      this.eventoForm.patchValue({
        slug: this.slugify(nome)
      }, { emitEvent: false });
    });

    const id = this.route.snapshot.paramMap.get('id');

    if (id) {
      this.titulo = 'Editar Evento';
      this.carregarEvento(+id);
    }
  }

  get sections(): FormArray {
    return this.eventoForm.get('sections') as FormArray;
  }

  addSection() {
    this.sections.push(
      this.fb.group({
        titulo: ['', Validators.required],
        ativo: [true],
        conteudoJson: ['{}']
      })
    );
  }

  removeSection(index: number) {
    this.sections.removeAt(index);
  }

  salvar() {
    if (this.eventoForm.valid) {
      console.log('Evento salvo:', this.eventoForm.value);
      this.router.navigate(['/admin/eventos']);
    } else {
      this.eventoForm.markAllAsTouched();
    }
  }

  cancelar() {
    this.router.navigate(['/admin/eventos']);
  }

  private carregarEvento(id: number) {
    this.http.get<any[]>('assets/eventos.json').subscribe(eventos => {
      const evento = eventos.find(e => e.id === id);
      if (evento) {
        this.eventoForm.patchValue(evento);
        evento.sections.forEach((sec: any) => {
          this.sections.push(this.fb.group(sec));
        });
      } else {
        console.warn('Evento não encontrado!');
      }
    });
  }

  // função para gerar slug
  private slugify(text: string): string {
    return text
      .toString()
      .normalize('NFD')                   // remove acentos
      .replace(/[\u0300-\u036f]/g, '')    // remove diacríticos
      .toLowerCase()
      .trim()
      .replace(/[^a-z0-9\s-]/g, '')       // remove caracteres inválidos
      .replace(/\s+/g, '-')               // espaços → hífen
      .replace(/-+/g, '-');               // múltiplos hífens → um só
  }
}
<div class="card shadow-sm">
    <div class="card-header bg-primary text-white">
        <h5 class="mb-0">{{ titulo }}</h5>
    </div>
    <div class="card-body">
        <form [formGroup]="eventoForm" (ngSubmit)="salvar()">
            <!-- Nome -->
            <div class="mb-3">
                <label for="nome" class="form-label">Nome</label>
                <input
                id="nome"
                type="text"
                class="form-control"
                formControlName="nome"
                [ngClass]="{'is-invalid': eventoForm.get('nome')?.invalid && eventoForm.get('nome')?.touched}"
                />
                <div class="invalid-feedback">O nome é obrigatório.</div>
            </div>

            <!-- Data Início, Data Fim e Status (na mesma linha) -->
            <div class="row">
                <div class="col-md-4 mb-3">
                    <label class="form-label">Data Início</label>
                    <input type="date" class="form-control" formControlName="dataInicio" />
                </div>

                <div class="col-md-4 mb-3">
                    <label class="form-label">Data Fim</label>
                    <input type="date" class="form-control" formControlName="dataFim" />
                </div>

                <div class="col-md-4 mb-3">
                    <label class="form-label">Status</label>
                    <select class="form-select" formControlName="status">
                        <option value="Criado">Criado</option>
                        <option value="Em andamento">Em andamento</option>
                        <option value="Cancelado">Cancelado</option>
                        <option value="Finalizado">Finalizado</option>
                    </select>
                </div>
            </div>

            <!-- Organizador Nome, Email e Contato (na mesma linha) -->
            <div class="row">
                <div class="col-md-4 mb-3">
                    <label class="form-label">Nome do Organizador</label>
                    <input type="text" class="form-control" formControlName="organizadorNome" />
                </div>

                <div class="col-md-4 mb-3">
                    <label class="form-label">E-mail do Organizador</label>
                    <input type="email" class="form-control" formControlName="organizadorEmail" />
                </div>

                <div class="col-md-4 mb-3">
                    <label class="form-label">Contato do Organizador</label>
                    <input type="text" class="form-control" formControlName="organizadorContato" />
                </div>
            </div>

            <!-- Sections Dinâmicas -->
            <div class="mb-3">
                <div class="d-flex justify-content-between align-items-center mb-2">
                    <h6>Sections</h6>
                    <button type="button" class="btn btn-sm btn-outline-success" (click)="addSection()">
                        <i class="bi bi-plus-circle"></i> Adicionar Section
                    </button>
                </div>

                <div formArrayName="sections">
                    <div
                    class="card mb-2"
                    *ngFor="let section of sections.controls; let i = index"
                    [formGroupName]="i"
                    >
                    <div class="card-body">
                        <div class="row">
                            <div class="col-md-6 mb-2">
                                <label class="form-label">Título</label>
                                <input type="text" class="form-control" formControlName="titulo" />
                            </div>
                            <div class="col-md-3 mb-2">
                                <label class="form-label">Ativo</label>
                                <div class="form-check">
                                    <input type="checkbox" class="form-check-input" formControlName="ativo" />
                                </div>
                            </div>
                            <div class="col-md-12 mb-2">
                                <label class="form-label">Conteúdo (JSON)</label>
                                <textarea rows="3" class="form-control" formControlName="conteudoJson"></textarea>
                            </div>
                        </div>
                        <button type="button" class="btn btn-sm btn-outline-danger" (click)="removeSection(i)">
                            <i class="bi bi-trash"></i> Remover
                        </button>
                    </div>
                </div>
            </div>
        </div>

        <!-- Botões -->
        <div class="d-flex justify-content-end">
            <button type="button" class="btn btn-secondary me-2" (click)="cancelar()">Cancelar</button>
            <button type="submit" class="btn btn-primary">Salvar</button>
        </div>
    </form>
</div>
</div>
